import React, { useState, useEffect, useContext } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { ApiContext } from '../App';

function ServiceList({ i18n }) {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const { type } = useParams();
  const { api } = useContext(ApiContext);

  useEffect(() => {
    const fetchServices = async () => {
      try {
        const response = await axios.get(`${api}/api/services`);
        const filteredServices = response.data.filter(service => service.type === type);
        setServices(filteredServices);
      } catch (err) {
        setError(i18n.service_fetch_error);
      } finally {
        setLoading(false);
      }
    };

    fetchServices();
  }, [type, api, i18n]);

  if (loading) return <div className="text-center mt-5">{i18n.loading_text}</div>;
  if (error) return <div className="text-center mt-5 text-danger">{error}</div>;

  return (
    <div>
      <h2 className="h3 mb-4 text-center">
        {type === 'workshop' ? i18n.workshops_title : i18n.car_wash_title}
      </h2>

      {services.length > 0 ? (
        <ul className="list-group">
          {services.map(service => (
            <li key={service.id} className="list-group-item d-flex justify-content-between align-items-center">
              <div>
                <h4 className="h5">{service.name}</h4>
                <p className="text-muted mb-0">{service.description}</p>
                <p className="fw-bold mt-1 mb-0">${service.price}</p>
              </div>
              <Link
                to={`/service/${service.id}`}
                className="btn btn-success"
              >
                {i18n.view_details_button}
              </Link>
            </li>
          ))}
        </ul>
      ) : (
        <p className="text-center text-muted">{i18n.no_services_found}</p>
      )}

      <div className="text-center mt-4">
        <Link to="/" className="text-primary">&larr; {i18n.back_to_home_button}</Link>
      </div>
    </div>
  );
}

export default ServiceList;
