import React, { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import { ApiContext } from "../App";

export default function ServiceList({ type }) {
  const { api } = useContext(ApiContext);
  const [services, setServices] = useState([]);

  useEffect(() => {
    fetch(`${api}/api/services`)
      .then(res => res.json())
      .then(data => setServices(data))
      .catch(() => setServices([]));
  }, [api]);

  const filtered = services.filter(s => s.type === type);

  return (
    <div>
      <h2>{type === "workshop" ? "Workshops" : "Car Wash"}</h2>
      {filtered.length === 0 && <p>No services found.</p>}
      {filtered.map(s => (
        <div key={s.id} className="card mb-3">
          <h4>{s.name}</h4>
          <p>{s.description}</p>
          <p>Price: ${s.price}</p>
          <Link to={`/book/${s.id}`}>Book Now</Link>
        </div>
      ))}
    </div>
  );
}
