import React, { useState, useEffect, useContext } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { ApiContext, AuthContext } from "../App";

export default function BookingForm({ i18n }) {
  const { api } = useContext(ApiContext);
  const { auth } = useContext(AuthContext);
  const navigate = useNavigate();
  const { id: serviceId } = useParams();

  const [service, setService] = useState(null);
  const [date, setDate] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    if (!auth.token) navigate("/login");
  }, [auth, navigate]);

  useEffect(() => {
    fetch(`${api}/api/services/${serviceId}`, { headers: { Accept: "application/json" } })
      .then((res) => res.json())
      .then((data) => setService(data))
      .catch(() => setMessage("Failed to load service details"));
  }, [api, serviceId]);

  const handleBooking = async (e) => {
    e.preventDefault();
    if (!auth.token || !service) return;

    try {
      const res = await fetch(`${api}/api/bookings`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${auth.token}`, // Sanctum token
        },
        body: JSON.stringify({
          service_id: service.id,
          date,
        }),
      });

      const data = await res.json();
      if (!res.ok) throw new Error(data.message || "Booking failed");

      setMessage("Booking confirmed!");
      setDate("");
    } catch (err) {
      console.error(err);
      setMessage(err.message);
    }
  };

  if (!service) return <div>Loading service...</div>;

  return (
    <div className="max-w-md mx-auto p-4 border rounded shadow">
      <h2 className="text-xl font-bold mb-4">{i18n?.bookService || "Book a Service"}</h2>
      {message && <p className="mb-2 text-blue-600">{message}</p>}

      <div className="mb-4">
        <h3 className="font-semibold">{service.name}</h3>
        <p>{service.description}</p>
        <p>Price: ${service.price}</p>
      </div>

      <form onSubmit={handleBooking} className="space-y-4">
        <div>
          <label className="block mb-1">{i18n?.chooseDate || "Choose Date:"}</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full border p-2 rounded"
            required
          />
        </div>

        <button type="submit" className="w-full bg-blue-500 text-white py-2 rounded">
          {i18n?.confirmBooking || "Confirm Booking"}
        </button>
      </form>
    </div>
  );
}
