import React, { useState, useEffect, useContext } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { ApiContext } from '../App';

function ServiceDetail({ i18n }) {
  const [service, setService] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const { id } = useParams();
  const { api } = useContext(ApiContext);

  useEffect(() => {
    const fetchService = async () => {
      try {
        const response = await axios.get(`${api}/api/services/${id}`);
        setService(response.data);
      } catch (err) {
        setError(i18n.service_fetch_error);
      } finally {
        setLoading(false);
      }
    };
    fetchService();
  }, [id, api, i18n]);

  if (loading) return <div className="text-center mt-5">{i18n.loading_text}</div>;
  if (error) return <div className="text-center mt-5 text-danger">{error}</div>;
  if (!service) return <div className="text-center mt-5">{i18n.service_not_found}</div>;

  return (
    <div className="card p-4 mx-auto" style={{ maxWidth: '500px' }}>
      <h2 className="card-title text-center h3">{service.name}</h2>
      <p className="text-muted text-center">{service.type === 'workshop' ? i18n.workshop_label : i18n.car_wash_label}</p>
      <div className="card-body">
        <p className="card-text">{service.description}</p>
        <p className="card-text"><strong>{i18n.price_label}:</strong> ${service.price}</p>

        <div className="mt-4 d-grid gap-2">
          <Link
            to={`/book/${service.id}`}
            className="btn btn-primary btn-lg"
          >
            {i18n.book_now_button}
          </Link>
          <Link
            to={`/services/${service.type}`}
            className="btn btn-outline-secondary"
          >
            &larr; {i18n.back_to_list_button}
          </Link>
        </div>
      </div>
    </div>
  );
}

export default ServiceDetail;
