import React, { useState, useContext } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { ApiContext, AuthContext } from '../App';

export default function Register({ i18n }) {
  const { api } = useContext(ApiContext); // Laravel API URL
  const { setAuth } = useContext(AuthContext);
  const navigate = useNavigate();

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [passwordConfirm, setPasswordConfirm] = useState('');
  const [message, setMessage] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    setMessage('');

    if (password !== passwordConfirm) {
      setMessage('Passwords do not match');
      return;
    }

    try {
      const res = await fetch(`${api}/api/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json' // ensures Laravel returns JSON
        },
        body: JSON.stringify({ name, email, password })
      });

      const data = await res.json();

      if (!res.ok) {
        // Laravel validation errors are usually in data.errors
        if (data.errors) {
          const allErrors = Object.values(data.errors).flat().join(' ');
          setMessage(allErrors);
        } else {
          setMessage(data.message || 'Registration failed');
        }
        return;
      }

      // Registration success, log in user
      // Use token if your backend returns one
      const token = data.token || 'dummy-token';
      setAuth({ user: data, token }); 
      localStorage.setItem('user', JSON.stringify(data));
      localStorage.setItem('token', token);

      navigate('/'); // redirect to home
    } catch (err) {
      setMessage('Registration failed: ' + err.message);
    }
  };

  return (
    <div className="max-w-md mx-auto p-4 border rounded shadow">
      <h2 className="text-xl font-bold mb-4">{i18n?.register || 'Register'}</h2>

      {message && <p className="mb-2 text-red-600">{message}</p>}

      <form onSubmit={handleRegister} className="space-y-4">
        <div>
          <label className="block mb-1">Name:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="w-full border p-2 rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full border p-2 rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full border p-2 rounded"
            required
          />
        </div>

        <div>
          <label className="block mb-1">Confirm Password:</label>
          <input
            type="password"
            value={passwordConfirm}
            onChange={(e) => setPasswordConfirm(e.target.value)}
            className="w-full border p-2 rounded"
            required
          />
        </div>

        <button
          type="submit"
          className="w-full bg-green-500 text-white py-2 rounded"
        >
          {i18n?.register || 'Register'}
        </button>
      </form>

      <p className="mt-3 text-sm">
        Already have an account?{' '}
        <Link to="/login" className="text-blue-600 underline">
          Login here
        </Link>
      </p>
    </div>
  );
}
