import React, { useState, useEffect, useContext } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { ApiContext } from "../App";

export default function BookingForm({ i18n }) {
  const { api } = useContext(ApiContext);
  const navigate = useNavigate();
  const { id: serviceIdParam } = useParams();

  const [services, setServices] = useState([]);
  const [selectedService, setSelectedService] = useState(serviceIdParam || "");
  const [date, setDate] = useState("");
  const [message, setMessage] = useState("");

  // Fetch all services from backend
  useEffect(() => {
    fetch(`${api}/api/services`, {
      headers: { "Accept": "application/json" },
    })
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) setServices(data);
      })
      .catch(() => setMessage("Error loading services"));
  }, [api]);

  const handleBooking = (e) => {
    e.preventDefault();

    if (!selectedService || !date) {
      setMessage("Please select a service and date.");
      return;
    }

    const bookingData = {
      service_id: selectedService,
      date,
      user_id: 1, // TEMP: use a real user ID if available
    };

fetch(`${api}/api/bookings`, {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${auth.token}` // user token from login
  },
  body: JSON.stringify({
    service_id: selectedService,
    date
  })
})

      .then(async (res) => {
        if (!res.ok) {
          const text = await res.text();
          throw new Error(text || "Booking failed");
        }
        return res.json();
      })
      .then((data) => {
        setMessage("Booking confirmed!");
        setSelectedService("");
        setDate("");
      })
      .catch((err) => setMessage("Booking failed: " + err.message));
  };

  return (
    <div className="max-w-md mx-auto p-4 border rounded shadow">
      <h2 className="text-xl font-bold mb-4">{i18n?.bookService || "Book a Service"}</h2>

      {message && <p className="mb-2 text-blue-600">{message}</p>}

      <form onSubmit={handleBooking} className="space-y-4">
        <div>
          <label className="block mb-1">{i18n?.selectService || "Select Service:"}</label>
          <select
            value={selectedService}
            onChange={(e) => setSelectedService(e.target.value)}
            className="w-full border p-2 rounded"
            required
          >
            <option value="">-- {i18n?.choose || "Choose"} --</option>
            {services.map((s) => (
              <option key={s.id} value={s.id}>
                {s.name} ({s.type}) - ${s.price}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label className="block mb-1">{i18n?.chooseDate || "Choose Date:"}</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full border p-2 rounded"
            required
          />
        </div>

        <button type="submit" className="w-full bg-blue-500 text-white py-2 rounded">
          {i18n?.confirmBooking || "Confirm Booking"}
        </button>
      </form>
    </div>
  );
}
