import React, { useState, useContext } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { ApiContext, AuthContext } from '../App';

export default function Register({ i18n }) {
  const { api } = useContext(ApiContext);
  const { setAuth } = useContext(AuthContext);
  const navigate = useNavigate();

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [passwordConfirm, setPasswordConfirm] = useState('');
  const [message, setMessage] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    if (password !== passwordConfirm) {
      setMessage('Passwords do not match');
      return;
    }

    try {
      const res = await fetch(`${api}/api/register`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          name,
          email,
          password,
          password_confirmation: passwordConfirm // Laravel expects this
        }),
      });

      const data = await res.json();

      if (!res.ok) {
        setMessage(data.message || 'Registration failed');
        return;
      }

      // Save user locally
      setAuth({ user: data.user });
      localStorage.setItem('user', JSON.stringify(data.user));

      navigate('/'); // redirect to home
    } catch (err) {
      setMessage('Registration failed: ' + err.message);
    }
  };

  return (
    <div className="max-w-md mx-auto p-4 border rounded shadow">
      <h2 className="text-xl font-bold mb-4">Register</h2>
      {message && <p className="mb-2 text-red-600">{message}</p>}

      <form onSubmit={handleRegister} className="space-y-4">
        <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} className="w-full border p-2 rounded" required />
        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} className="w-full border p-2 rounded" required />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} className="w-full border p-2 rounded" required />
        <input type="password" placeholder="Confirm Password" value={passwordConfirm} onChange={(e) => setPasswordConfirm(e.target.value)} className="w-full border p-2 rounded" required />

        <button type="submit" className="w-full bg-green-500 text-white py-2 rounded">Register</button>
      </form>

      <p className="mt-3 text-sm">
        Already have an account? <Link to="/login" className="text-blue-600 underline">Login here</Link>
      </p>
    </div>
  );
}
