import React, { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { ApiContext, AuthContext } from "../App";

export default function Profile({ i18n }) {
  const { auth } = useContext(AuthContext);
  const { api } = useContext(ApiContext);
  const navigate = useNavigate();
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    if (!auth.token) {
      // Redirect to login if not authenticated
      navigate("/login");
      return;
    }

    // Fetch profile info
    fetch(`${api}/api/user`, {
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${auth.token}`,
      },
    })
      .then((res) => res.json())
      .then(setProfile)
      .catch(() => navigate("/login"));
  }, [auth, api, navigate]);

  if (!auth.token || !profile) return <p>Loading...</p>;

  return (
    <div className="max-w-md mx-auto p-4 border rounded shadow">
      <h2 className="text-xl font-bold mb-4">{i18n.profile}</h2>
      <p>
        <strong>{i18n.name}:</strong> {profile.name}
      </p>
      <p>
        <strong>{i18n.email}:</strong> {profile.email}
      </p>
    </div>
  );
}
