import React, {useState, useEffect, useContext} from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ApiContext } from '../App';

export default function BookingForm({i18n}){
  const {id} = useParams();
  const {api} = useContext(ApiContext);
  const nav = useNavigate();
  const [service,setService] = useState(null);
  const [form,setForm] = useState({name:'',email:'',date:''});
  useEffect(()=>{
    fetch(`${api}/api/services`).then(r=>r.json()).then(list=>setService(list.find(s=>s.id==id)));
  },[api,id]);

  const submit = async (e)=>{
    e.preventDefault();
    // simple register / login by email: register then create booking
    try{
      const reg = await fetch(`${api}/api/auth/register`,{
        method:'POST',headers:{'Content-Type':'application/json'},body:JSON.stringify({email:form.email,name:form.name})
      }).then(r=>r.json());
      const token = reg.token;
      const booking = await fetch(`${api}/api/bookings`,{
        method:'POST',
        headers:{'Content-Type':'application/json','Authorization':'Bearer '+token},
        body:JSON.stringify({service_id: id, date: form.date})
      }).then(r=>r.json());
      alert('Booking confirmed! ID: '+booking.id);
      nav('/profile');
    }catch(err){
      console.error(err);
      alert('Error creating booking (check backend).');
    }
  };

  if(!service) return <div>Loading...</div>;
  return (
    <div>
      <h3>{i18n.booking}: {service.name}</h3>
      <form onSubmit={submit}>
        <div className="mb-2"><label>{i18n.name}</label><input className="form-control" value={form.name} onChange={e=>setForm({...form,name:e.target.value})} required/></div>
        <div className="mb-2"><label>{i18n.email}</label><input type="email" className="form-control" value={form.email} onChange={e=>setForm({...form,email:e.target.value})} required/></div>
        <div className="mb-2"><label>{i18n.date}</label><input type="datetime-local" className="form-control" value={form.date} onChange={e=>setForm({...form,date:e.target.value})} required/></div>
        <button className="btn btn-success" type="submit">{i18n.confirm}</button>
      </form>
    </div>
  );
}
