import React, { useState, useEffect } from "react";

function Booking() {
  const [services, setServices] = useState([]);
  const [selectedService, setSelectedService] = useState("");
  const [date, setDate] = useState("");
  const [status, setStatus] = useState("pending");
  const [message, setMessage] = useState("");

  const API_URL = process.env.REACT_APP_API_URL || "http://127.0.0.1:8000";

  // Fetch services from backend
  useEffect(() => {
    fetch(`${API_URL}/services`)
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setServices(data);
        }
      })
      .catch(() => setMessage("Error loading services"));
  }, [API_URL]);

  // Submit booking
  const handleBooking = (e) => {
    e.preventDefault();

    const bookingData = {
      service_id: selectedService,
      date,
      status,
    };

    fetch(`${API_URL}/bookings`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        // nëse ke auth token nga login, shtoje këtu
        // "Authorization": `Bearer ${localStorage.getItem("token")}`
      },
      body: JSON.stringify(bookingData),
    })
      .then((res) => res.json())
      .then(() => {
        setMessage("Booking confirmed!");
        setSelectedService("");
        setDate("");
      })
      .catch(() => setMessage("Booking failed"));
  };

  return (
    <div className="max-w-md mx-auto p-4">
      <h2 className="text-xl font-bold mb-4">Book a Service</h2>

      {message && <p className="mb-2 text-blue-600">{message}</p>}

      <form onSubmit={handleBooking} className="space-y-4">
        <div>
          <label className="block mb-1">Select Service:</label>
          <select
            value={selectedService}
            onChange={(e) => setSelectedService(e.target.value)}
            className="w-full border p-2 rounded"
            required
          >
            <option value="">-- Choose --</option>
            {services.map((s) => (
              <option key={s.id} value={s.id}>
                {s.name} ({s.type}) - ${s.price}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label className="block mb-1">Choose Date:</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full border p-2 rounded"
            required
          />
        </div>

        <button
          type="submit"
          className="w-full bg-blue-500 text-white py-2 rounded"
        >
          Confirm Booking
        </button>
      </form>
    </div>
  );
}

export default Booking;
