import React, { useState, useEffect, useContext } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { ApiContext, AuthContext } from "../App";

export default function BookingForm({ i18n }) {
  const { api } = useContext(ApiContext);
  const { auth } = useContext(AuthContext);
  const navigate = useNavigate();
  const { id: serviceId } = useParams();

  const [date, setDate] = useState("");
  const [message, setMessage] = useState("");

  useEffect(() => {
    if (!auth.token) navigate("/login");
  }, [auth, navigate]);

  const handleBooking = async (e) => {
    e.preventDefault();
    if (!auth.token) return;

    try {
      const res = await fetch(`${api}/api/bookings`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${auth.token}`
        },
        body: JSON.stringify({
          service_id: serviceId,
          date
        })
      });

      if (!res.ok) throw new Error("Booking failed");

      const data = await res.json();
      setMessage(`Booking confirmed for ${data.date}!`);
      setDate("");
    } catch (err) {
      console.error(err);
      setMessage("Booking failed. Try again.");
    }
  };

  return (
    <div className="max-w-md mx-auto p-4 border rounded shadow">
      <h2 className="text-xl font-bold mb-4">{i18n?.bookService || "Book a Service"}</h2>

      {message && <p className="mb-2 text-blue-600">{message}</p>}

      <form onSubmit={handleBooking} className="space-y-4">
        <div>
          <label className="block mb-1">{i18n?.chooseDate || "Choose Date:"}</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full border p-2 rounded"
            required
          />
        </div>

        <button type="submit" className="w-full bg-blue-500 text-white py-2 rounded">
          {i18n?.confirmBooking || "Confirm Booking"}
        </button>
      </form>
    </div>
  );
}
