import React, { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { ApiContext, AuthContext } from "../App";

export default function BookingForm({ i18n }) {
  const { api } = useContext(ApiContext);
  const { auth } = useContext(AuthContext);
  const navigate = useNavigate();

  const [services, setServices] = useState([]);
  const [selectedService, setSelectedService] = useState("");
  const [date, setDate] = useState("");
  const [message, setMessage] = useState("");

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!auth.token) {
      navigate("/login");
    }
  }, [auth, navigate]);

  // Fetch services from backend
  useEffect(() => {
    fetch(`${api}/api/services`, {
      headers: { "Accept": "application/json" }
    })
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) setServices(data);
      })
      .catch(() => setMessage("Error loading services"));
  }, [api]);

  // Submit booking
  const handleBooking = (e) => {
    e.preventDefault();
    if (!auth.token) return;

    const bookingData = {
      service_id: selectedService,
      date,
      status: "pending"
    };

    fetch(`${api}/api/bookings`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${auth.token}`
      },
      body: JSON.stringify(bookingData)
    })
      .then((res) => res.json())
      .then((data) => {
        if (data.id) {
          setMessage("Booking confirmed!");
          setSelectedService("");
          setDate("");
        } else {
          setMessage(data.message || "Booking failed");
        }
      })
      .catch(() => setMessage("Booking failed"));
  };

  return (
    <div className="max-w-md mx-auto p-4 border rounded shadow">
      <h2 className="text-xl font-bold mb-4">{i18n?.bookService || "Book a Service"}</h2>

      {message && <p className="mb-2 text-blue-600">{message}</p>}

      <form onSubmit={handleBooking} className="space-y-4">
        <div>
          <label className="block mb-1">{i18n?.selectService || "Select Service:"}</label>
          <select
            value={selectedService}
            onChange={(e) => setSelectedService(e.target.value)}
            className="w-full border p-2 rounded"
            required
          >
            <option value="">-- {i18n?.choose || "Choose"} --</option>
            {services.map((s) => (
              <option key={s.id} value={s.id}>
                {s.name} ({s.type}) - ${s.price}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label className="block mb-1">{i18n?.chooseDate || "Choose Date:"}</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            className="w-full border p-2 rounded"
            required
          />
        </div>

        <button
          type="submit"
          className="w-full bg-blue-500 text-white py-2 rounded"
        >
          {i18n?.confirmBooking || "Confirm Booking"}
        </button>
      </form>
    </div>
  );
}
