import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { ApiContext } from '../App';

function BookingForm({ i18n }) {
  const [date, setDate] = useState('');
  const [message, setMessage] = useState('');
  const [service, setService] = useState(null);

  const { id } = useParams();
  const navigate = useNavigate();
  const { api } = useContext(ApiContext);

  // Fetch the service details to display on the form
  useEffect(() => {
    const fetchService = async () => {
      try {
        const response = await axios.get(`${api}/api/services/${id}`);
        setService(response.data);
      } catch (err) {
        setMessage(i18n.service_fetch_error);
      }
    };
    fetchService();
  }, [id, api, i18n]);

  const handleBooking = async (e) => {
    e.preventDefault();
    setMessage('');
    try {
      const bookingData = {
        service_id: id,
        date,
        // user_id and status will be handled by the Laravel backend
      };
      await axios.post(`${api}/api/bookings`, bookingData);
      setMessage(i18n.booking_success_message);
      // Optional: redirect to a confirmation or profile page
      setTimeout(() => {
        navigate('/profile'); // Redirect to a profile page to see the booking
      }, 2000);
    } catch (err) {
      setMessage(i18n.booking_error_message);
    }
  };

  if (!service) return <div className="text-center mt-5">{i18n.loading_text}</div>;

  return (
    <div className="card p-4 mx-auto" style={{ maxWidth: '500px' }}>
      <h2 className="card-title h3 text-center">{i18n.book_title}</h2>
      <p className="text-center text-muted">{i18n.booking_for_label}: {service.name}</p>

      {message && <div className={`alert ${message.includes('success') ? 'alert-success' : 'alert-danger'} mt-3`}>{message}</div>}

      <form onSubmit={handleBooking} className="mt-4">
        <div className="mb-3">
          <label htmlFor="date" className="form-label">{i18n.date_label}</label>
          <input
            type="date"
            id="date"
            className="form-control"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>

        <button type="submit" className="btn btn-primary w-100">
          {i18n.confirm_booking_button}
        </button>
      </form>

      <div className="text-center mt-3">
        <Link to={`/service/${id}`} className="text-secondary">&larr; {i18n.back_to_details_button}</Link>
      </div>
    </div>
  );
}

export default BookingForm;
